import React, { useState } from 'react';
import './ProjectGenerator.css'; // Ensure your CSS file path is correct

const Generator = ({ onSubmit }) => {
  const [projectName, setProjectName] = useState('');
  const [projectId, setProjectId] = useState('');
  const [projectPassword, setProjectPassword] = useState('');
  const [mode, setMode] = useState('create'); // 'create' or 'login'

  const handleProjectNameChange = (e) => setProjectName(e.target.value);
  const handleProjectIdChange = (e) => setProjectId(e.target.value);
  const handleProjectPasswordChange = (e) => setProjectPassword(e.target.value);
  const handleModeChange = (e) => setMode(e.target.value);

  const handleSubmit = (e) => {
    e.preventDefault();
    const data = {
      projectName,
      projectId,
      projectPassword,
      mode,
    };
    onSubmit(data);
    // Reset the form
    setProjectName('');
    setProjectId('');
    setProjectPassword('');
  };

  return (
    <div className="project-generator">
      <form onSubmit={handleSubmit}>
        <h2>Project Generator</h2>
        <div className="radio-buttons">
          <label>
            <input
              type="radio"
              value="create"
              checked={mode === 'create'}
              onChange={handleModeChange}
            />
            Create New Project
          </label>
          <label>
            <input
              type="radio"
              value="login"
              checked={mode === 'login'}
              onChange={handleModeChange}
            />
            Login to Existing Project
          </label>
        </div>
        {mode === 'create' && (
          <div className="input-group">
            <label>Project Name</label>
            <input
              type="text"
              value={projectName}
              onChange={handleProjectNameChange}
              required
            />
          </div>
        )}
        {mode === 'login' && (
          <div className="input-group">
            <label>Project ID</label>
            <input
              type="text"
              value={projectId}
              onChange={handleProjectIdChange}
              required
            />
          </div>
        )}
        <div className="input-group">
          <label>Project Password</label>
          <input
            type="password"
            value={projectPassword}
            onChange={handleProjectPasswordChange}
            required
          />
        </div>
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default Generator;
