import React, { useState } from 'react';
import axios from 'axios';
import './ProjectGenerator.css'; // Ensure your CSS file path is correct

const Generator = () => {
  const [projectName, setProjectName] = useState('');
  const [projectId, setProjectId] = useState('');
  const [projectPassword, setProjectPassword] = useState('');
  const [mode, setMode] = useState('create'); // 'create' or 'login'

  const handleGenerate = () => {
    const id = `proj-${Math.random().toString(36).substr(2, 9)}`;
    const password = Math.random().toString(36).substr(2, 12);
    setProjectId(id);
    setProjectPassword(password);
  };

  const handleSave = async () => {
    if (projectName && projectId && projectPassword) {
      try {
        await axios.post('http://your-backend-url.com/projects', {
          name: projectName,
          id: projectId,
          password: projectPassword
        });
        alert('Project saved successfully!');
      } catch (error) {
        console.error('Error saving project:', error);
        alert('Failed to save project.');
      }
    } else {
      alert('Please generate project ID and password first.');
    }
  };

  const handleLogin = async () => {
    try {
      const response = await axios.post('http://your-backend-url.com/login', {
        id: projectId,
        password: projectPassword
      });
      alert('Login successful!');
    } catch (error) {
      console.error('Error logging in:', error);
      alert('Login failed.');
    }
  };

  return (
    <div className="project-generator">
      <h2>Project Generator</h2>
      <div>
        <label>
          <input
            type="radio"
            value="create"
            checked={mode === 'create'}
            onChange={() => setMode('create')}
          />
          Create New Project
        </label>
        <label>
          <input
            type="radio"
            value="login"
            checked={mode === 'login'}
            onChange={() => setMode('login')}
          />
          Login to Existing Project
        </label>
      </div>

      {mode === 'create' && (
        <div>
          <input
            type="text"
            placeholder="Enter Project Name..."
            value={projectName}
            onChange={(e) => setProjectName(e.target.value)}
          />
          <button onClick={handleGenerate}>Generate</button>
          {projectId && (
            <div>
              <p><strong>Project ID:</strong> {projectId}</p>
              <p><strong>Project Password:</strong> {projectPassword}</p>
            </div>
          )}
          <button onClick={handleSave}>Save to Backend</button>
        </div>
      )}

      {mode === 'login' && (
        <div>
          <input
            type="text"
            placeholder="Enter Project ID..."
            value={projectId}
            onChange={(e) => setProjectId(e.target.value)}
          />
          <input
            type="password"
            placeholder="Enter Project Password..."
            value={projectPassword}
            onChange={(e) => setProjectPassword(e.target.value)}
          />
          <button onClick={handleLogin}>Login</button>
        </div>
      )}
    </div>
  );
};

export default Generator;
